/**
 * IR Builder - Core Schema Primitives
 *
 * Foundational schema building functions used by all IR builder modules.
 * This is the bottom layer of the IR builder architecture - it has no
 * dependencies on other IR builder modules.
 *
 * **Pure Functions:**
 * All functions are pure - deterministic output, no side effects.
 *
 * **Library Types:**
 * Uses SchemaObject, ReferenceObject from openapi3-ts/oas31 exclusively.
 *
 * @module ir-builder.core
 * @internal
 */

import type { SchemaObject, ReferenceObject } from 'openapi3-ts/oas31';
import { isReferenceObject } from 'openapi3-ts/oas31';
import type { IRSchema, IRSchemaNode } from './ir-schema.js';
import { IRSchemaProperties } from './ir-schema.js';
import type { IRBuildContext } from './ir-builder.types.js';

/**
 * Build IR schema from OpenAPI SchemaObject or ReferenceObject.
 * Recursively processes primitives, objects, arrays, compositions, and references.
 * Core schema building function used throughout the IR builder.
 *
 * @param schema - OpenAPI schema or reference
 * @param context - Build context with document and path information
 * @returns IR schema with full metadata
 * @internal
 */
export function buildIRSchema(
  schema: SchemaObject | ReferenceObject,
  context: IRBuildContext,
): IRSchema {
  // Handle $ref - preserve reference, metadata is computed later
  if (isReferenceObject(schema)) {
    // For references, create a minimal schema object for metadata building
    const emptySchema: SchemaObject = {};
    const metadata = buildIRSchemaNode(emptySchema, context);

    return {
      $ref: schema.$ref,
      metadata,
    };
  }

  // Build schema metadata
  const metadata = buildIRSchemaNode(schema, context);

  // Build base IR schema with primitive properties
  const irSchema = buildBaseIRSchema(schema, metadata);

  // Add complex schema properties
  addObjectProperties(schema, context, irSchema);
  addArrayItems(schema, context, irSchema);
  addCompositionSchemas(schema, context, irSchema);

  return irSchema;
}

/**
 * Build IR schema node metadata (required, nullable, validations, dependencies).
 * Used during code generation to produce appropriate Zod schemas.
 *
 * @param schema - OpenAPI schema object
 * @param context - Build context
 * @returns IR schema node metadata
 * @internal
 */
export function buildIRSchemaNode(schema: SchemaObject, context: IRBuildContext): IRSchemaNode {
  // Determine nullability from OAS 3.1 type arrays
  const nullable = Array.isArray(schema.type) && schema.type.includes('null');

  return {
    required: context.required,
    nullable,
    ...(schema.description !== undefined && { description: schema.description }),
    ...(schema.default !== undefined && { default: schema.default }),
    dependencyGraph: {
      references: [],
      referencedBy: [],
      depth: 0,
    },
    zodChain: {
      presence: context.required ? 'required' : 'optional',
      validations: [],
      defaults: [],
    },
    circularReferences: [],
  };
}

/**
 * Build base IR schema with primitive properties.
 * @internal
 */
function buildBaseIRSchema(schema: SchemaObject, metadata: IRSchemaNode): IRSchema {
  const irSchema: IRSchema = { metadata };
  addTypeInfo(schema, irSchema);
  addDocumentation(schema, irSchema);
  addConstraints(schema, irSchema);
  return irSchema;
}

/** @internal */
function addTypeInfo(schema: SchemaObject, irSchema: IRSchema): void {
  if (schema.type !== undefined) {
    irSchema.type = schema.type;
  }
  if (schema.format !== undefined) {
    irSchema.format = schema.format;
  }
}

/** @internal */
function addDocumentation(schema: SchemaObject, irSchema: IRSchema): void {
  if (schema.description !== undefined) {
    irSchema.description = schema.description;
  }
  if (schema.default !== undefined) {
    irSchema.default = schema.default;
  }
  if (schema.example !== undefined) {
    irSchema.example = schema.example;
  }
  if (schema.examples !== undefined) {
    irSchema.examples = schema.examples;
  }
}

/** @internal */
function addConstraints(schema: SchemaObject, irSchema: IRSchema): void {
  if (schema.minimum !== undefined) {
    irSchema.minimum = schema.minimum;
  }
  if (schema.maximum !== undefined) {
    irSchema.maximum = schema.maximum;
  }
  if (schema.exclusiveMinimum !== undefined) {
    irSchema.exclusiveMinimum = schema.exclusiveMinimum;
  }
  if (schema.exclusiveMaximum !== undefined) {
    irSchema.exclusiveMaximum = schema.exclusiveMaximum;
  }
  if (schema.multipleOf !== undefined) {
    irSchema.multipleOf = schema.multipleOf;
  }
  if (schema.minLength !== undefined) {
    irSchema.minLength = schema.minLength;
  }
  if (schema.maxLength !== undefined) {
    irSchema.maxLength = schema.maxLength;
  }
  if (schema.pattern !== undefined) {
    irSchema.pattern = schema.pattern;
  }
  if (schema.minItems !== undefined) {
    irSchema.minItems = schema.minItems;
  }
  if (schema.maxItems !== undefined) {
    irSchema.maxItems = schema.maxItems;
  }
  if (schema.uniqueItems !== undefined) {
    irSchema.uniqueItems = schema.uniqueItems;
  }
  // Preserve enum values (critical for data integrity)
  if (schema.enum !== undefined && Array.isArray(schema.enum)) {
    irSchema.enum = Array.from(schema.enum);
  }
}

/**
 * Add object properties to IR schema (recursively builds property schemas).
 * @internal
 */
function addObjectProperties(
  schema: SchemaObject,
  context: IRBuildContext,
  irSchema: IRSchema,
): void {
  if (!schema.properties) {
    return;
  }

  const requiredFields = schema.required ?? [];
  const propsRecord: Record<string, IRSchema> = {};

  for (const [propName, propSchema] of Object.entries(schema.properties)) {
    const isRequired = requiredFields.includes(propName);
    const propContext: IRBuildContext = {
      ...context,
      path: [...context.path, 'properties', propName],
      required: isRequired,
    };

    propsRecord[propName] = buildIRSchema(propSchema, propContext);
  }

  irSchema.properties = new IRSchemaProperties(propsRecord);
  irSchema.required = requiredFields;
}

/**
 * Add array items to IR schema (recursively builds items schema).
 * @internal
 */
function addArrayItems(schema: SchemaObject, context: IRBuildContext, irSchema: IRSchema): void {
  if (!schema.items) {
    return;
  }

  const itemsContext: IRBuildContext = {
    ...context,
    path: [...context.path, 'items'],
    required: false,
  };

  irSchema.items = buildIRSchema(schema.items, itemsContext);
}

/**
 * Add composition schemas (allOf, oneOf, anyOf) - recursively builds members.
 * @internal
 */
function addCompositionSchemas(
  schema: SchemaObject,
  context: IRBuildContext,
  irSchema: IRSchema,
): void {
  if (schema.allOf) {
    irSchema.allOf = schema.allOf.map((subSchema, index) => {
      const subContext: IRBuildContext = {
        ...context,
        path: [...context.path, 'allOf', String(index)],
        required: false,
      };
      return buildIRSchema(subSchema, subContext);
    });
  }

  if (schema.oneOf) {
    irSchema.oneOf = schema.oneOf.map((subSchema, index) => {
      const subContext: IRBuildContext = {
        ...context,
        path: [...context.path, 'oneOf', String(index)],
        required: false,
      };
      return buildIRSchema(subSchema, subContext);
    });
  }

  if (schema.anyOf) {
    irSchema.anyOf = schema.anyOf.map((subSchema, index) => {
      const subContext: IRBuildContext = {
        ...context,
        path: [...context.path, 'anyOf', String(index)],
        required: false,
      };
      return buildIRSchema(subSchema, subContext);
    });
  }
}

/**
 * IR Builder - Operations Orchestration
 *
 * Handles extraction and building of operations from OpenAPI paths.
 * Orchestrates parameter, request body, and response building.
 *
 * @module ir-builder.operations
 * @internal
 */

import type {
  OpenAPIObject,
  PathItemObject,
  OperationObject,
  SecurityRequirementObject,
} from 'openapi3-ts/oas31';
import { isReferenceObject } from 'openapi3-ts/oas31';
import type { IROperation, IRSecurityRequirement } from './ir-schema.js';
import type { HttpMethod } from '../endpoints/definition.types.js';
import type { IRBuildContext } from './ir-builder.types.js';
import { buildIRParameters } from './ir-builder.parameters.js';
import { buildIRRequestBody } from './ir-builder.request-body.js';
import { buildIRResponses } from './ir-builder.responses.js';

/**
 * Build IR operations from OpenAPI paths object.
 *
 * Extracts all HTTP operations from the paths section, converting each
 * path+method combination into an IROperation structure with metadata.
 *
 * @param paths - OpenAPI paths object (may be undefined)
 * @param components - OpenAPI components for reference resolution (may be undefined)
 * @returns Array of IR operations
 *
 * @example
 * ```typescript
 * const paths: PathsObject = {
 *   '/users': {
 *     get: { operationId: 'getUsers', responses: { '200': { description: 'Success' } } },
 *     post: { operationId: 'createUser', responses: { '201': { description: 'Created' } } },
 *   },
 * };
 * const operations = buildIROperations(paths);
 * console.log(operations.length); // 2
 * ```
 *
 * @internal
 */
export function buildIROperations(doc: OpenAPIObject): IROperation[] {
  if (!doc.paths) {
    return [];
  }

  const operations: IROperation[] = [];

  for (const [path, pathItem] of Object.entries(doc.paths)) {
    if (pathItem) {
      const pathOperations = extractPathOperations(path, pathItem, doc);
      operations.push(...pathOperations);
    }
  }

  return operations;
}

/**
 * Extract all operations for a single path.
 * @internal
 */
function extractPathOperations(
  path: string,
  pathItem: PathItemObject,
  doc: OpenAPIObject,
): IROperation[] {
  const operations: IROperation[] = [];
  const httpMethods: HttpMethod[] = ['get', 'post', 'put', 'patch', 'delete', 'head', 'options'];

  for (const method of httpMethods) {
    const operation = pathItem[method];
    if (!operation || typeof operation !== 'object' || isReferenceObject(operation)) {
      continue;
    }

    // Merge path-level parameters with operation-level parameters
    // Operation-level parameters take precedence (though usually they are distinct)
    const mergedParameters = [...(pathItem.parameters || []), ...(operation.parameters || [])];

    // Create a new operation object with merged parameters to avoid mutating the original
    // Use destructuring to remove parameters from the base operation, then add merged ones if present
    const { parameters: _unused, ...operationWithoutParams } = operation;
    const operationWithParams: OperationObject = {
      ...operationWithoutParams,
      ...(mergedParameters.length > 0 ? { parameters: mergedParameters } : {}),
    };

    const irOperation = buildIROperation(method, path, operationWithParams, doc);
    operations.push(irOperation);
  }

  return operations;
}

/**
 * Build single IR operation from OpenAPI operation object.
 *
 * Converts an OpenAPI operation definition into an IROperation structure,
 * extracting operationId, method, path, parameters, responses, and metadata.
 *
 * @param method - HTTP method for this operation
 * @param path - API path with parameter placeholders
 * @param operation - OpenAPI operation object
 * @param doc - Full OpenAPI document for reference resolution
 * @returns IR operation with extracted metadata
 *
 * @internal
 */
function buildIROperation(
  method: HttpMethod,
  path: string,
  operation: OperationObject,
  doc: OpenAPIObject,
): IROperation {
  // Build minimal context for schema resolution
  const context: IRBuildContext = {
    doc,
    path: [path, method],
    required: false,
  };

  // Build core operation structure
  const irOperation: IROperation = {
    operationId: operation.operationId ?? `${method}${path.replace(/\W/g, '')}`,
    method,
    path,
    parameters: buildIRParameters(operation.parameters, context),
    responses: buildIRResponses(operation.responses, context),
  };

  // Add optional fields
  addOptionalOperationFields(irOperation, operation, context);

  return irOperation;
}

/**
 * Add optional fields to IR operation if present in source.
 * @internal
 */
function addOptionalOperationFields(
  irOperation: IROperation,
  operation: OperationObject,
  context: IRBuildContext,
): void {
  if (operation.summary) {
    irOperation.summary = operation.summary;
  }
  if (operation.description) {
    irOperation.description = operation.description;
  }
  if (operation.requestBody) {
    irOperation.requestBody = buildIRRequestBody(operation.requestBody, context);
  }
  if (operation.security) {
    irOperation.security = buildIRSecurity(operation.security);
  }
  if (operation.tags) {
    irOperation.tags = operation.tags;
  }
  // Check deprecated status - property is part of OpenAPI 3.1 spec
  // Using Reflect to avoid false-positive deprecation linter warnings
  const deprecatedKey = 'deprecated';
  if (Object.hasOwn(operation, deprecatedKey)) {
    const deprecatedValue = Reflect.get(operation, deprecatedKey);
    if (deprecatedValue === true) {
      Reflect.set(irOperation, deprecatedKey, true);
    }
  }
}

/**
 * Build IR security requirements from OpenAPI security array.
 *
 * Converts security requirement objects into IRSecurityRequirement structures
 * with scheme names and OAuth2 scopes.
 *
 * @param security - OpenAPI security requirements array
 * @returns Array of IR security requirements
 *
 * @internal
 */
function buildIRSecurity(security: SecurityRequirementObject[]): IRSecurityRequirement[] {
  return security.flatMap((securityReq): IRSecurityRequirement[] => {
    return Object.entries(securityReq).map(([schemeName, scopes]): IRSecurityRequirement => {
      return {
        schemeName,
        scopes: scopes ?? [],
      };
    });
  });
}

/**
 * IR Builder - Component Schema Extraction
 *
 * Handles extraction of schemas from OpenAPI components section.
 * Focused on component-level schema extraction and organization.
 *
 * @module ir-builder.schemas
 * @internal
 */

import type { ComponentsObject } from 'openapi3-ts/oas31';
import type { IRComponent, IRSchema, IRSecuritySchemeComponent } from './ir-schema.js';
import type { IRBuildContext } from './ir-builder.types.js';
import { buildIRSchema } from './ir-builder.core.js';
import { buildSingleParameter } from './ir-builder.parameters.js';
import { buildSingleResponse } from './ir-builder.responses.js';
import { buildIRRequestBody } from './ir-builder.request-body.js';

/**
 * Build IR components from OpenAPI components object.
 *
 * Extracts schemas from the components section, converting each to an
 * IRComponent structure with full schema information and metadata.
 * Detects circular references after building all schemas.
 *
 * @param components - OpenAPI components object (may be undefined)
 * @returns Array of IR components with circular references detected
 *
 * @example
 * ```typescript
 * const components: ComponentsObject = {
 *   schemas: {
 *     Pet: { type: 'object', properties: { name: { type: 'string' } } },
 *     Error: { type: 'object', properties: { message: { type: 'string' } } },
 *   },
 * };
 *
 * const irComponents = buildIRSchemas(components);
 * // Returns array with 2 IRComponent objects
 * ```
 *
 * @public
 */
export function buildIRSchemas(components: ComponentsObject | undefined): IRComponent[] {
  if (!components) {
    return [];
  }

  const irComponents: IRComponent[] = [];

  // Process Schemas
  if (components.schemas) {
    const schemaNames = Object.keys(components.schemas);
    const schemaComponents = schemaNames.map((name) => {
      const schema = components.schemas![name];
      if (!schema) {
        throw new Error(`Schema '${name}' is undefined`);
      }

      // Build context for this schema
      const context: IRBuildContext = {
        doc: { openapi: '3.1.0', info: { title: '', version: '' }, paths: {} },
        path: ['#', 'components', 'schemas', name],
        required: false,
      };

      // Build IR schema from OpenAPI schema
      const irSchema = buildIRSchema(schema, context);

      return {
        type: 'schema' as const,
        name,
        schema: irSchema,
        metadata: irSchema.metadata,
      };
    });
    irComponents.push(...schemaComponents);
  }

  // Process Security Schemes
  if (components.securitySchemes) {
    const securityNames = Object.keys(components.securitySchemes);
    const securityComponents: IRSecuritySchemeComponent[] = securityNames.map((name) => {
      const scheme = components.securitySchemes![name];
      if (!scheme) {
        throw new Error(`Security scheme '${name}' is undefined`);
      }
      return {
        type: 'securityScheme' as const,
        name,
        scheme,
      };
    });
    irComponents.push(...securityComponents);
  }

  // Process Parameters
  if (components.parameters) {
    const paramNames = Object.keys(components.parameters);
    const paramComponents = paramNames.map((name): IRComponent => {
      const param = components.parameters![name];
      if (!param) {
        throw new Error(`Parameter '${name}' is undefined`);
      }
      // Create a temporary context for the parameter
      const context: IRBuildContext = {
        doc: { openapi: '3.1.0', info: { title: '', version: '' }, paths: {} },
        path: ['#', 'components', 'parameters', name],
        required: false,
      };
      return {
        type: 'parameter',
        name,
        parameter: buildSingleParameter(param, context),
      };
    });
    irComponents.push(...paramComponents);
  }

  // Process Responses
  if (components.responses) {
    const responseNames = Object.keys(components.responses);
    const responseComponents = responseNames.map((name): IRComponent => {
      const response = components.responses![name];
      if (!response) {
        throw new Error(`Response '${name}' is undefined`);
      }
      // Create a temporary context for the response
      const context: IRBuildContext = {
        doc: { openapi: '3.1.0', info: { title: '', version: '' }, paths: {} },
        path: ['#', 'components', 'responses', name],
        required: false,
      };
      // For responses, we need a status code, but components don't have one.
      // We use 'default' or a placeholder as it's a reusable component.
      return {
        type: 'response',
        name,
        response: buildSingleResponse('default', response, context),
      };
    });
    irComponents.push(...responseComponents);
  }

  // Process Request Bodies
  if (components.requestBodies) {
    const requestBodyNames = Object.keys(components.requestBodies);
    const requestBodyComponents = requestBodyNames.map((name): IRComponent => {
      const requestBody = components.requestBodies![name];
      if (!requestBody) {
        throw new Error(`RequestBody '${name}' is undefined`);
      }
      // Create a temporary context for the requestBody
      const context: IRBuildContext = {
        doc: { openapi: '3.1.0', info: { title: '', version: '' }, paths: {} },
        path: ['#', 'components', 'requestBodies', name],
        required: false,
      };
      return {
        type: 'requestBody',
        name,
        requestBody: buildIRRequestBody(requestBody, context),
      };
    });
    irComponents.push(...requestBodyComponents);
  }

  // Detect and populate circular references (only for schema components)
  detectCircularReferences(irComponents);

  return irComponents;
}

/**
 * Detect circular references in schemas using depth-first search.
 *
 * Populates the `circularReferences` array on each schema's metadata
 * when cycles are detected. Handles both self-referencing (A → A)
 * and mutually referencing (A → B → A) patterns.
 *
 * @param components - Array of IR components to analyze
 *
 * @internal
 */
function detectCircularReferences(components: IRComponent[]): void {
  // Filter for schema components only
  const schemaComponents = components.filter(
    (c): c is IRComponent & { type: 'schema' } => c.type === 'schema',
  );

  // Build dependency graph: schema name → referenced schema names
  const dependencyGraph = new Map<string, Set<string>>();

  for (const component of schemaComponents) {
    const refs = extractSchemaReferences(component.schema);
    dependencyGraph.set(component.name, refs);
  }

  // For each schema, detect cycles using DFS
  for (const component of schemaComponents) {
    const cycles = findCyclesFrom(component.name, dependencyGraph);
    if (cycles.length > 0 && component.schema.metadata) {
      // Convert schema names to full refs
      component.schema.metadata.circularReferences = cycles.map(
        (name) => `#/components/schemas/${name}`,
      );
    }
  }
}

/**
 * Extract all schema references from an IR schema.
 *
 * @param irSchema - IR schema to analyze
 * @returns Set of referenced schema names
 *
 * @internal
 */
function extractSchemaReferences(irSchema: IRSchema): Set<string> {
  const refs = new Set<string>();

  // Check direct $ref
  if (irSchema.$ref) {
    const refName = extractSchemaNameFromRef(irSchema.$ref);
    if (refName) {
      refs.add(refName);
    }
  }

  // Check properties (for object schemas)
  const propertyRefs = extractPropertyReferences(irSchema.properties);
  propertyRefs.forEach((ref) => refs.add(ref));

  // Check array items
  const itemRefs = extractItemsReferences(irSchema.items);
  itemRefs.forEach((ref) => refs.add(ref));

  // Check composition schemas (allOf, oneOf, anyOf)
  const compositionRefs = extractCompositionReferences(irSchema);
  compositionRefs.forEach((ref) => refs.add(ref));

  return refs;
}

/**
 * Extract references from object properties.
 *
 * @param properties - Schema properties to analyze
 * @returns Set of referenced schema names
 *
 * @internal
 */
function extractPropertyReferences(properties: IRSchema['properties']): Set<string> {
  const refs = new Set<string>();

  if (properties) {
    const values = Object.values(properties);
    for (const propSchema of values) {
      const propRefs = extractSchemaReferences(propSchema);
      propRefs.forEach((ref) => refs.add(ref));
    }
  }

  return refs;
}

/**
 * Extract references from array items.
 *
 * @param items - Schema items to analyze
 * @returns Set of referenced schema names
 *
 * @internal
 */
function extractItemsReferences(items: IRSchema['items']): Set<string> {
  // Fail-fast: empty set for null/undefined items
  if (!items) {
    return new Set<string>();
  }

  const refs = new Set<string>();

  if (Array.isArray(items)) {
    for (const item of items) {
      if (isIRSchemaLike(item)) {
        const itemRefs = extractSchemaReferences(item);
        itemRefs.forEach((ref) => refs.add(ref));
      }
    }
  } else if (isIRSchemaLike(items)) {
    const itemRefs = extractSchemaReferences(items);
    itemRefs.forEach((ref) => refs.add(ref));
  }

  return refs;
}

/**
 * Extract references from composition schemas (allOf, oneOf, anyOf).
 *
 * @param irSchema - IR schema to analyze
 * @returns Set of referenced schema names
 *
 * @internal
 */
function extractCompositionReferences(irSchema: IRSchema): Set<string> {
  const refs = new Set<string>();
  const compositionKeys: ('allOf' | 'oneOf' | 'anyOf')[] = ['allOf', 'oneOf', 'anyOf'];

  for (const key of compositionKeys) {
    const schemas = irSchema[key];
    if (schemas && Array.isArray(schemas)) {
      const compositionRefs = extractReferencesFromSchemas(schemas);
      compositionRefs.forEach((ref) => refs.add(ref));
    }
  }

  return refs;
}

/**
 * Extract references from an array of schemas.
 *
 * @param schemas - Array of schemas to analyze
 * @returns Set of referenced schema names
 *
 * @internal
 */
function extractReferencesFromSchemas(schemas: unknown[]): Set<string> {
  const refs = new Set<string>();

  for (const schema of schemas) {
    if (isIRSchemaLike(schema)) {
      const schemaRefs = extractSchemaReferences(schema);
      schemaRefs.forEach((ref) => refs.add(ref));
    }
  }

  return refs;
}

/**
 * Type guard to check if value looks like an IRSchema.
 *
 * @param value - Value to check
 * @returns True if value has IRSchema structure
 *
 * @internal
 */
function isIRSchemaLike(value: unknown): value is IRSchema {
  return value !== null && typeof value === 'object' && 'metadata' in value;
}

/**
 * Extract schema name from a $ref string.
 *
 * @param ref - OpenAPI $ref string
 * @returns Schema name or undefined
 *
 * @internal
 */
function extractSchemaNameFromRef(ref: string): string | undefined {
  // Handle standard refs: #/components/schemas/SchemaName
  const standardRegex = /#\/components\/schemas\/([^/]+)$/;
  const standardMatch = standardRegex.exec(ref);
  if (standardMatch) {
    return standardMatch[1];
  }
  // Handle x-ext refs: #/x-ext/{hash}/components/schemas/SchemaName
  const xExtRegex = /#\/x-ext\/[^/]+\/components\/schemas\/([^/]+)$/;
  const xExtMatch = xExtRegex.exec(ref);
  if (xExtMatch) {
    return xExtMatch[1];
  }
  return undefined;
}

/**
 * Find all cycles starting from a given schema using DFS.
 *
 * @param startName - Schema name to start from
 * @param graph - Dependency graph (name → referenced names)
 * @returns Array of schema names that form cycles with the start schema
 *
 * @internal
 */
function findCyclesFrom(startName: string, graph: Map<string, Set<string>>): string[] {
  const cycles = new Set<string>();
  const visited = new Set<string>();
  const pathStack = new Set<string>();

  function dfs(currentName: string): void {
    if (pathStack.has(currentName)) {
      // Found a cycle - mark this node as circular
      cycles.add(currentName);
      return;
    }

    if (visited.has(currentName)) {
      return;
    }

    visited.add(currentName);
    pathStack.add(currentName);

    const dependencies = graph.get(currentName) || new Set();
    for (const depName of dependencies) {
      dfs(depName);
    }

    pathStack.delete(currentName);
  }

  dfs(startName);

  return Array.from(cycles);
}

import type {
  OpenAPIObject,
  PathsObject,
  PathItemObject,
  OperationObject,
  SchemaObject,
  ReferenceObject,
  ParameterObject,
  RequestBodyObject,
  ResponseObject,
  ResponsesObject,
} from 'openapi3-ts/oas31';
import type {
  IRDocument,
  IRSchema,
  IRComponent,
  IROperation,
  IRParameter,
  IRResponse,
  IRRequestBody,
} from './ir-schema.js';

/**
 * Converts an IRDocument back to an OpenAPIObject.
 * This enables round-trip fidelity testing and proves the IR is lossless.
 *
 * @param ir - The IRDocument to convert
 * @returns The reconstructed OpenAPIObject
 */
export function convertIRToOpenAPI(ir: IRDocument): OpenAPIObject {
  const components = convertComponents(ir.components);
  return {
    openapi: ir.openApiVersion,
    info: ir.info,
    ...(Object.keys(components).length > 0 ? { components } : {}),
    paths: convertOperations(ir.operations),
  };
}

function convertComponents(components: IRComponent[]): NonNullable<OpenAPIObject['components']> {
  const result: NonNullable<OpenAPIObject['components']> = {};

  for (const component of components) {
    if (component.type === 'schema') {
      result.schemas = result.schemas || {};
      result.schemas[component.name] = convertSchema(component.schema);
    } else if (component.type === 'securityScheme') {
      result.securitySchemes = result.securitySchemes || {};
      result.securitySchemes[component.name] = component.scheme;
    } else if (component.type === 'parameter') {
      result.parameters = result.parameters || {};
      result.parameters[component.name] = convertParameter(component.parameter);
    } else if (component.type === 'response') {
      result.responses = result.responses || {};
      result.responses[component.name] = convertResponse(component.response);
    } else if (component.type === 'requestBody') {
      result.requestBodies = result.requestBodies || {};
      result.requestBodies[component.name] = convertRequestBody(component.requestBody);
    }
  }

  return result;
}

function convertSchema(irSchema: IRSchema): SchemaObject | ReferenceObject {
  // Create a shallow copy to avoid mutating the input
  const schema: any = { ...irSchema };

  // Remove IR-specific metadata
  delete schema.metadata;

  // Recursively convert children
  if (schema.properties) {
    const props: Record<string, SchemaObject | ReferenceObject> = {};
    for (const [key, value] of Object.entries(schema.properties)) {
      props[key] = convertSchema(value as IRSchema);
    }
    schema.properties = props;
  }

  if (schema.items) {
    if (Array.isArray(schema.items)) {
      schema.items = schema.items.map(convertSchema);
    } else {
      schema.items = convertSchema(schema.items);
    }
  }

  if (schema.allOf) {
    schema.allOf = schema.allOf.map(convertSchema);
  }
  if (schema.oneOf) {
    schema.oneOf = schema.oneOf.map(convertSchema);
  }
  if (schema.anyOf) {
    schema.anyOf = schema.anyOf.map(convertSchema);
  }
  if (schema.not) {
    schema.not = convertSchema(schema.not);
  }
  if (schema.additionalProperties && typeof schema.additionalProperties === 'object') {
    schema.additionalProperties = convertSchema(schema.additionalProperties);
  }

  if (Array.isArray(schema.required) && schema.required.length === 0) {
    delete schema.required;
  }

  return schema as SchemaObject | ReferenceObject;
}

function convertOperations(operations: IROperation[]): PathsObject {
  const paths: PathsObject = {};

  for (const op of operations) {
    if (!paths[op.path]) {
      paths[op.path] = {};
    }

    const pathItem = paths[op.path] as PathItemObject;
    const operationObject: OperationObject = {
      operationId: op.operationId,
      ...(op.summary ? { summary: op.summary } : {}),
      ...(op.description ? { description: op.description } : {}),
      ...(op.tags ? { tags: op.tags } : {}),
      parameters: op.parameters.map(convertParameter),
      responses: convertResponses(op.responses),
      ...(op.deprecated ? { deprecated: op.deprecated } : {}),
      ...(op.security
        ? { security: op.security.map((sec) => ({ [sec.schemeName]: sec.scopes })) }
        : {}),
    };

    if (op.requestBody) {
      operationObject.requestBody = convertRequestBody(op.requestBody);
    }

    pathItem[op.method] = operationObject;
  }

  return paths;
}

function convertParameter(param: IRParameter): ParameterObject | ReferenceObject {
  // ParameterObject
  const p: ParameterObject = {
    name: param.name,
    in: param.in,
    required: param.required,
    schema: convertSchema(param.schema),
    ...(param.description ? { description: param.description } : {}),
    ...(param.deprecated ? { deprecated: param.deprecated } : {}),
  };

  if (param.style) {
    p.style = param.style;
  }
  if (param.explode !== undefined) {
    p.explode = param.explode;
  }
  if (param.allowReserved !== undefined) {
    p.allowReserved = param.allowReserved;
  }
  if (param.example !== undefined) {
    p.example = param.example;
  }
  if (param.examples) {
    p.examples = param.examples;
  }

  return p;
}

function convertRequestBody(body: IRRequestBody): RequestBodyObject | ReferenceObject {
  // RequestBodyObject
  return {
    required: body.required,
    ...(body.description ? { description: body.description } : {}),
    content: Object.entries(body.content).reduce(
      (acc, [key, value]) => {
        acc[key] = {
          schema: convertSchema(value.schema),
          ...(value.example !== undefined ? { example: value.example } : {}),
          ...(value.examples ? { examples: value.examples } : {}),
        };
        return acc;
      },
      {} as Record<string, any>,
    ),
  };
}

function convertResponses(responses: IRResponse[]): ResponsesObject {
  // ResponsesObject
  const result: ResponsesObject = {};

  for (const resp of responses) {
    const responseObj: ResponseObject = {
      description: resp.description || '',
    };

    if (resp.content) {
      responseObj.content = Object.entries(resp.content).reduce(
        (acc, [key, value]) => {
          acc[key] = {
            schema: convertSchema(value.schema),
            ...(value.example !== undefined ? { example: value.example } : {}),
            ...(value.examples ? { examples: value.examples } : {}),
          };
          return acc;
        },
        {} as Record<string, any>,
      );
    }

    if (resp.headers) {
      responseObj.headers = Object.entries(resp.headers).reduce(
        (acc, [key, value]) => {
          acc[key] = { schema: convertSchema(value) };
          return acc;
        },
        {} as Record<string, any>,
      );
    }

    result[resp.statusCode] = responseObj;
  }

  return result;
}

function convertResponse(resp: IRResponse): ResponseObject | ReferenceObject {
  // ResponseObject
  const responseObj: ResponseObject = {
    description: resp.description || '',
  };

  if (resp.content) {
    responseObj.content = Object.entries(resp.content).reduce(
      (acc, [key, value]) => {
        acc[key] = {
          schema: convertSchema(value.schema),
          ...(value.example !== undefined ? { example: value.example } : {}),
          ...(value.examples ? { examples: value.examples } : {}),
        };
        return acc;
      },
      {} as Record<string, any>,
    );
  }

  if (resp.headers) {
    responseObj.headers = Object.entries(resp.headers).reduce(
      (acc, [key, value]) => {
        acc[key] = { schema: convertSchema(value) };
        return acc;
      },
      {} as Record<string, any>,
    );
  }

  return responseObj;
}

import type { IRDocument } from './ir-schema.js';

/**
 * Serializes an IRDocument to a JSON string.
 * Uses pretty-printing (2 spaces) for readability in debug artifacts.
 *
 * @param ir - The IRDocument to serialize
 * @returns JSON string representation of the IR
 */
export function serializeIR(ir: IRDocument): string {
  return JSON.stringify(ir, null, 2);
}

/**
 * Deserializes a JSON string to an IRDocument.
 *
 * @param json - The JSON string to deserialize
 * @returns The parsed IRDocument
 * @throws SyntaxError if the JSON is invalid
 */
export function deserializeIR(json: string): IRDocument {
  return JSON.parse(json) as IRDocument;
}

